import { useState } from "react";
import emailjs from '@emailjs/browser';
import {useRef} from 'react';

const Form = () => {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("")
    const [subject, setSubject] = useState("")
    const [message, setMessage] = useState("")

    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs
          .sendForm('service_qinbgjs', 'template_wk9532m', form.current, {
            publicKey: 'S3gifjZd7NgefU05j',
          })
          .then(
            () => {
              console.log('SUCCESS!');
            },
            (error) => {
              console.log('FAILED...', error.text);
            },
          );
      };

    const handleSubmit = (e) => {
        e.preventDefault()
        window.open(`mailto:alexnham11@gmail.com?subject=${subject}&body=${message}`);


    }

    return ( 
        <form ref={form} className=" text-black mt-20 flex flex-col w-5/6" onSubmit={sendEmail}>
                <input
                    type="text"
                    name="name"
                    required
                    value={name}
                    onChange={(e) => setName(e.target.value)} 
                    placeholder="Name"
                    />
                <input
                    type="text"
                    n="email"
                    required
                    value={email}
                    onChange={(e) => setEmail(e.target.value)} 
                    placeholder="Email"
                    />
                <input
                                    className="mb-4 h-10"

                    type="text"
                    subject="subject"
                    required
                    value={subject}
                    onChange={(e) => setSubject(e.target.value)} 
                    placeholder="Subject"
                    />

                <textarea
                    required
                    name="message"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Body"
                />
            <button>Send</button>
                </form>
     );
}

export default Form;
