import { useState } from "react";
import emailjs from '@emailjs/browser';
import {useRef} from 'react';

const Form = () => {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("")
    const [subject, setSubject] = useState("")
    const [message, setMessage] = useState("")

    const form = useRef();

    const handleSubmit = (e) => {
        e.preventDefault()
        window.open(`mailto:alexnham11@gmail.com?subject=${subject}&body=${message}`);


    }

    return ( 
        <form className=" text-black mt-20 flex flex-col w-5/6" onSubmit={handleSubmit}>
                <input
                    type="text"
                    required
                    value={name}
                    onChange={(e) => setName(e.target.value)} 
                    placeholder="Name"
                    />
                <input
                    type="text"
                    required
                    value={email}
                    onChange={(e) => setEmail(e.target.value)} 
                    placeholder="Email"
                    />
                <input
                                    className="mb-4 h-10"

                    type="text"
                    required
                    value={subject}
                    onChange={(e) => setSubject(e.target.value)} 
                    placeholder="Subject"
                    />

                <textarea
                    required
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Body"
                />
            <button>Send</button>
                </form>
     );
}

export default Form;
